                        }
                    },
                    "savecard": false
                },
                "AuthenticationInformation": {
                    "Code": "{{sessionid}}",
                    "ReferenceId": "", 
                    "TransactionId": ""
                }
            }
        ],
        "deliveryaddress": {
            "street": "{{$randomStreetName}}",
            "number": "{{$randomInt}}",
            "complement": "{{$randomStreetAddress}}",
            "neighborhood": "{{$randomAdjective}} {{$randomWord}}",
            "city": "{{$randomCity}}",
            "state": "{{randomState}}",
            "country": "BR",
            "postalcode": "{{randomCep}}",
            "phone": "{{$randomPhoneNumber}}"
        },
        "countrycode": "BR",
        "amount": {{amount}},
        "currency": "BRL"
    },
    "frontdata": {
        "useragent": "{{$randomUserAgent}}",
        "IpAddress": "{{$randomIP}}"
    },
    "istest": false,
    "isfromfrontend": false
}

